		if (array_key_exists($keys[2], $arr)) $this->setOrgId($arr[$keys[2]]);
		if (array_key_exists($keys[3], $arr)) $this->setPositionId($arr[$keys[3]]);
		if (array_key_exists($keys[4], $arr)) $this->setTimestamp($arr[$keys[4]]);
	}

	/**
	 * Build a Criteria object containing the values of all modified columns in this object.
	 *
	 * @return     Criteria The Criteria object containing all modified values.
	 */
	public function buildCriteria()
	{
		$criteria = new Criteria(MemberlogPeer::DATABASE_NAME);

		if ($this->isColumnModified(MemberlogPeer::LOG_ID)) $criteria->add(MemberlogPeer::LOG_ID, $this->log_id);
		if ($this->isColumnModified(MemberlogPeer::USER_ID)) $criteria->add(MemberlogPeer::USER_ID, $this->user_id);
		if ($this->isColumnModified(MemberlogPeer::ORG_ID)) $criteria->add(MemberlogPeer::ORG_ID, $this->org_id);
		if ($this->isColumnModified(MemberlogPeer::POSITION_ID)) $criteria->add(MemberlogPeer::POSITION_ID, $this->position_id);
		if ($this->isColumnModified(MemberlogPeer::TIMESTAMP)) $criteria->add(MemberlogPeer::TIMESTAMP, $this->timestamp);

		return $criteria;
	}

